// src/components/GrainyBackground.js
import React, { useEffect, useRef } from 'react';
import grained from 'grained';
import 'grained/grained.css'; // Import the grained styles

const GrainyBackground = () => {
  const backgroundRef = useRef();

  useEffect(() => {
    const options = {
      animate: true,
      patternWidth: 100,
      patternHeight: 100,
      grainOpacity: 0.1,
      grainDensity: 1,
      grainWidth: 0.5,
      grainHeight: 0.5,
    };

    grained(backgroundRef.current, options);
  }, []);

  return <div ref={backgroundRef} className="grainy-background"></div>;
};

export default GrainyBackground;
