import React from "react"
import GradientBackground from "../GradientBackground/GradientBackground"
import ScreenRecorderWithGIFConversion from "../ScreenrecordToGif/screenRecordToGif"
import styled from 'styled-components';
import GrainyBackground from "../GrainyBackground/GrainyBackground"
import imglyRemoveBackground from "@imgly/background-removal"
import { useState,useEffect} from "react"; 


export default function PortfolioCard(props){
    const {backgroundColor,color1,color2,color3} = props;
    const [imageSrc, setImageSrc] = useState('');

    const PortfolioCardContainer = styled.div`
        display:flex;
        justify-content:center;
        height:90vh;
        opacity:80%;
        border-radius:10px;
        background-color:${backgroundColor};
        `;
    const PortfolioImage =styled.img`
        position: absolute;
        width:33%;
        height:auto;
        border-radius:10px;
        background-image:'transparent';
    `;
    useEffect(() => {
        let imageSrc = process.env.PUBLIC_URL + "/portfoliocardDP.png";

        imglyRemoveBackground(imageSrc).then((blob) => {
          // The result is a blob encoded as PNG. It can be converted to a URL to be used as HTMLImage.src
          const url = URL.createObjectURL(blob);
          setImageSrc(url);
        });
      }, []);
    return(
        <PortfolioCardContainer> 
            <PortfolioImage src={imageSrc} alt="temporary dp photo" />
            {/* <GradientBackground name='Hello' opacity='0.7' borderRadius= '10px' width='130px' height='60px' ></GradientBackground> */}
            <GrainyBackground baseFrequency="0.6" color1={color1} color2={color2} color3={color3}/>
        </PortfolioCardContainer>
    )
}
