import React, { useRef, useState } from 'react';
import Whammy from 'whammy';
import GIF from 'gif.js-upgrade';

const ScreenRecorderWithGIFConversion = () => {
  const videoRef = useRef();
  const [recording, setRecording] = useState(false);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getDisplayMedia({ video: true });
      const recorder = new MediaRecorder(stream);

      const chunks = [];
      recorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      recorder.onstop = () => {
        const webmBlob = new Blob(chunks, { type: 'video/webm' });
        const webmUrl = URL.createObjectURL(webmBlob);
        videoRef.current.src = webmUrl;

        convertWebMToGIF(webmBlob);
      };

      recorder.start();
      setRecording(true);

      setTimeout(() => {
        recorder.stop();
        stream.getTracks().forEach((track) => track.stop());
        setRecording(false);
      }, 5000); // Recording for exactly 5 seconds
    } catch (error) {
      console.error('Error starting recording:', error);
    }
  };

  const convertWebMToGIF = (webmBlob) => {
    const videoElement = document.createElement('video');
    videoElement.src = URL.createObjectURL(webmBlob);
    videoElement.addEventListener('loadeddata', async () => {
      const gif = new GIF({ workers: 2, quality: 10 });

      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');

      // Set canvas dimensions to the video dimensions
      canvas.width = videoElement.videoWidth;
      canvas.height = videoElement.videoHeight;

      for (let i = 0; i < videoElement.duration * 25; i++) {
        ctx.drawImage(
          videoElement,
          0,
          0,
          videoElement.videoWidth,
          videoElement.videoHeight
        );

        // Get the current frame as imageData
        const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);

        // Add the imageData as a frame to the GIF
        gif.addFrame(imageData, { delay: 40 }); // Adjust the delay as needed (25 frames per second)
      }

      gif.on('finished', (blob) => {
        const gifUrl = URL.createObjectURL(blob);
        videoRef.current.src = gifUrl;
      });

      gif.render();
    });
  };

  return (
    <div>
      <button onClick={startRecording} disabled={recording}>
        {recording ? 'Recording...' : 'Start Recording'}
      </button>
      <video ref={videoRef} controls style={{ height:'100px',marginTop: '10px' }} />
    </div>
  );
};

export default ScreenRecorderWithGIFConversion;
