// import logo from './logo.svg';
// import './App.css';
// import GradientBackground from './Components/GradientBackground/GradientBackground';
// import PortfolioCard from './Components/PortfolioCard/PortfolioCard';
// function App() {
//   return (
//     <div className='App-container'>
//       <PortfolioCard></PortfolioCard>
//       <GradientBackground display='none' width='100%' height='100vh'></GradientBackground>
//     </div>


//   );
// }

// export default App;
import { Cloudinary } from '@cloudinary/url-gen';
import { AdvancedVideo, lazyload } from '@cloudinary/react';
import { scale, fill } from '@cloudinary/url-gen/actions/resize';
import { AutoFocus } from '@cloudinary/url-gen/qualifiers/autoFocus';
import { FocusOn } from '@cloudinary/url-gen/qualifiers/focusOn';
import { autoGravity } from '@cloudinary/url-gen/qualifiers/gravity';

import './App.css';

import videos from './videos.json';

const cld = new Cloudinary({
  cloud: {
    cloudName: 'dymsz1apj',
  },
});

function App() {
  return (
    <main className="main">
      <div className="container">
        <h1 className="title">Cloudinary React SDK AdvancedVideo Component</h1>
      </div>

      <div className="container">
        <h2>Default</h2>
        <p>No additional configuration or settings.</p>
        <ul className="videos">
          {videos.map((video) => {
            return (
              <li key={video.id}>
                <AdvancedVideo controls cldVid={cld.video(video.video)} />
              </li>
            );
          })}
        </ul>
      </div>

      <div className="container">
        <h2>Optimized</h2>
        <p>
          Format of auto (<code>f_auto</code>) and quality of auto (
          <code>q_auto</code>).
        </p>
        <ul className="videos">
          {videos.map((video) => {
            return (
              <li key={video.id}>
                <AdvancedVideo
                  controls
                  cldVid={cld
                    .video(video.video)
                    .delivery("q_auto")
                    .format("auto")}
                />
              </li>
            );
          })}
        </ul>
      </div>

      <div className="container">
        <h2>Dynamic Cropping</h2>
        <p>
          Uses AI to identify the dominant object in the video and tracks them
        </p>
        <ul className="videos">
          <li>
            <h3>Original Video </h3>
            <p>no additional configuration or settings.</p>
            <AdvancedVideo
              controls
              cldVid={
                cld.video("videos/apartment-tour")
                  .resize(scale().width(400))
                  .delivery("q_auto")
                  .format("auto")
              }
              autoPlay
              loop
            />
          </li>

          <li>
            <h3>Youtube like settings</h3>
            <p>Aspect Ratio 16:9, auto gravity and auto focus on faces</p>
            <AdvancedVideo
              controls
              cldVid={
                cld.video("videos/apartment-tour")
                  .resize(fill().width(400)
                    .aspectRatio("16:9")
                    .gravity(
                      autoGravity()
                        .autoFocus(AutoFocus.focusOn(FocusOn.face()))
                    )
                  )
                  .delivery("q_auto")
                  .format("auto")
              }
              autoPlay
              loop
            />
          </li>

          <li>
            <h3>Mobile Video</h3>
            <p>Aspect Ratio 9:16, auto gravity and auto focus on faces</p>
            <AdvancedVideo
              controls
              cldVid={
                cld.video("videos/apartment-tour")
                  .resize(fill().width(400)
                    .aspectRatio("9:16")
                    .gravity(
                      autoGravity()
                        .autoFocus(AutoFocus.focusOn(FocusOn.face()))
                    )
                  )
                  .delivery("q_auto")
                  .format("auto")
              }
              autoPlay
              loop
            />
          </li>

          <li>
            <h3>Square Video </h3>
            <p>Aspect Ratio 1:1 , auto gravity and auto focus on faces</p>
            <AdvancedVideo 
              controls
              cldVid={
                cld.video("videos/apartment-tour")
                  .resize(fill().width(400)
                    .aspectRatio("1:1")
                    .gravity(
                      autoGravity()
                        .autoFocus(AutoFocus.focusOn(FocusOn.face()))
                    )
                  )
                  .delivery("q_auto")
                  .format("auto")
              }
              autoPlay
              loop
            />
          </li>
        </ul>
      </div>

      <div className="container">
        <h2>Plugins</h2>
        <p>Lazy loading and low quality placeholders.</p>
        <ul className="videos">
          {videos.map((video) => {
            return (
              <li key={video.id}>
                <AdvancedVideo
                  controls
                  cldVid={cld
                    .video(video.video)
                    .delivery("q_auto")
                    .format("auto")}
                  plugins={[lazyload()]}
                />
              </li>
            );
          })}
        </ul>
      </div>

      <div className="container">
        <h2>Cloudinary-Generated Poster</h2>
        <p>
          Uses <code>cld.image</code> to generate a video thumbnail as the{" "}
          <code>poster</code> prop.
        </p>
        <ul className="videos">
          {videos.map((video) => {
            return (
              <li key={video.id}>
                <AdvancedVideo
                  controls
                  cldVid={cld
                    .video(video.video)
                    .delivery("q_auto")
                    .format("auto")}
                  poster={cld
                    .image(video.video)
                    .setAssetType("video")
                    .delivery("q_auto")
                    .format("auto:image")
                    .toURL()}
                />
              </li>
            );
          })}
        </ul>
      </div>

      <div className="container">
        <h2>Auto-Play Video Preview</h2>
        <p>
          Autoplays video with a Cloudinary-generated preview via{" "}
          <code>e_preview:duration_4</code>.
        </p>
        <ul className="videos">
          {videos.map((video) => {
            return (
              <li key={video.id}>
                <AdvancedVideo
                  autoPlay
                  muted
                  loop
                  cldVid={cld
                    .video(video.video)
                    .effect("e_preview:duration_4")
                    .delivery("q_auto")
                    .format("auto")}
                />
              </li>
            );
          })}
        </ul>
      </div>

      <div className="container">
        <h2>Resources</h2>
        <p>
          <a href="https://github.com/colbyfayock/cloudinary-examples/tree/main/examples/react-advanced-video">
            See the code on github.com.
          </a>
        </p>
        <ul>
          {videos.map((video) => {
            return (
              <li key={video.id}>
                {video.title}:{" "}
                <a href={video.link} rel="noreferrer">
                  {video.link}
                </a>
              </li>
            );
          })}
        </ul>
      </div>
    </main>
  );
}

export default App;
