// src/components/GrainedBackground.js
import React, { useEffect, useRef } from 'react';

const GrainedBackground = ({ containerId }) => {
  const containerRef = useRef();

  useEffect(() => {
    // Initialize grained.js
    const options = {
      animate: true,
      patternWidth: 100,
      patternHeight: 100,
      grainOpacity: 0.05,
      grainDensity: 1,
      grainWidth: 1,
      grainHeight: 1,
    };

    window.grained(containerId, options);

    // Additional styling to keep other elements on top
    const container = document.getElementById(containerId);
    if (container) {
      container.style.position = 'relative';
      container.style.overflow = 'hidden';
    }
  }, [containerId]);

  return <div ref={containerRef} id={containerId} className="grained-container"></div>;
};

export default GrainedBackground;
