import { Cloudinary } from '@cloudinary/url-gen';
import { AdvancedVideo } from '@cloudinary/react';
import { scale, fill } from '@cloudinary/url-gen/actions/resize';
import { AutoFocus } from '@cloudinary/url-gen/qualifiers/autoFocus';
import { FocusOn } from '@cloudinary/url-gen/qualifiers/focusOn';
import { autoGravity } from '@cloudinary/url-gen/qualifiers/gravity';

const cld = new Cloudinary({
  cloud: {
    cloudName: 'your-cloud-name', // Replace with your Cloudinary cloud name
  },
});

const localVideoUrl = './pexels-tima-miroshnichenko-6077718 (2160p).mp4';

function App() {
  return (
    <div className="container">
      <h2>Local Video</h2>
      <p>Using a local MP4 file as the video source.</p>
      <AdvancedVideo
        controls
        cldVid={
          cld.video(localVideoUrl)
            .resize(fill().width(400)
              .aspectRatio("16:9")
              .gravity(
                autoGravity()
                  .autoFocus(AutoFocus.focusOn(FocusOn.face()))
              )
            )
            .delivery("q_auto")
            .format("auto")
        }
        autoPlay
        loop
      />
    </div>
  );
}

export default App;
