import React, { useState } from "react"
import GradientBackground from "../GradientBackground/GradientBackground"
import ScreenRecorderWithGIFConversion from "../ScreenrecordToGif/screenRecordToGif"
import styled from 'styled-components';
import GrainyBackground from "../GrainyBackground/GrainyBackground"


export default function PortfolioCard(props){
    const {backgroundColor,color1,color2,color3} = props;
    const [image, setImage]=useState(null);
    const [bgRemovedImage, setBgremove]=useState(null);

    const PortfolioCardContainer = styled.div`
        display:flex;
        justify-content:center;
        height:90vh;
        opacity:80%;
        border-radius:10px;
        background-color:${backgroundColor};
        `;
    const PortfolioImage =styled.img`
        position: absolute;
        width:33%;
        height:auto;
        border-radius:10px;
        background-image:'#000';
    `;

    const handleChangebg = () => {
        setImage(process.env.PUBLIC_URL + "/portfoliocardDP.png");

        const apiKey="H6pix9SSieswBr34SDzE6CRe";        
        const url= 'https://api.remove.bg/v1.0/removebg';
        const formData= new FormData();
        formData.append('image_file', fs.createReadStream(image), path.basename(image));
        formData.append("size", "auto");

        fetch(url, {
            method: "POST",
            headers: {
             "X-Api-Key": apiKey,
            },
            body: formData,
            })
            .then((res) => res.blob())
            .then((blob) => {
            const reader = new FileReader();
            reader.onloadend = () => setBgremove(reader.result);
            reader.readAsDataURL(blob);
            })

        .catch((error) => console.error(error));
        };  

    return(
        <PortfolioCardContainer> 
            <button onClick={handleChangebg}>Remove Bg</button>
            {bgRemovedImage &&
                <PortfolioImage src={bgRemovedImage} alt="temporary dp photo" />
            }
            {/* <GradientBackground name='Hello' opacity='0.7' borderRadius= '10px' width='130px' height='60px' ></GradientBackground> */}
            <GrainyBackground baseFrequency="0.6" color1={color1} color2={color2} color3={color3}/>
        </PortfolioCardContainer>
    )
}
