import React, { useState } from "react"
import GradientBackground from "../GradientBackground/GradientBackground"
import ScreenRecorderWithGIFConversion from "../ScreenrecordToGif/screenRecordToGif"
import styled from 'styled-components';
import GrainyBackground from "../GrainyBackground/GrainyBackground"


export default function PortfolioCard(props){
    const {backgroundColor,color1,color2,color3} = props;
    const image=process.env.PUBLIC_URL + "/portfoliocardDP-removeBg.png";
    const PortfolioCardContainer = styled.div`
        display:flex;
        justify-content:center;
        align-items:center;
        height:90vh;
        width:29%;
        opacity:80%;
        border-radius:10px;
        background: linear-gradient(135deg, ${backgroundColor}, ${backgroundColor});

    `;
    const PortfolioImage =styled.img`
        display:flex;
        justify-content:center; 
        position: absolute;
        border-radius:10px;
        width:23%;
    `;

    return(
        <PortfolioCardContainer> 
            {/* <GradientBackground name='Hello' opacity='0.7' borderRadius= '10px' width='130px' height='60px' ></GradientBackground> */}
            <GrainyBackground baseFrequency="0.6" color1={color1} color2={color2} color3={color3}/>
            <div class="blob-cont" >
            <BlobComponent
              color={color1}
              position={{ top: '200px', left: '100px', height: '200px', width: '200px' }}
              animation="eastNorthWest 8s infinite ease"
            />
              <BlobComponent
              color={color2}
              position={{ top: '80px', right: '-20px', height: '200px', width: '200px' }}
              animation="eastNorth 8s infinite ease"
            />
              <BlobComponent
              color={color3}
              position={{ top: '300px', right: '0', height: '250px', width: '200px' }}
              animation="northWest 8s infinite ease"
            />
        </div>
            <PortfolioImage src={image} alt="temporary dp photo" />

        </PortfolioCardContainer>
    )
}
