import styled from 'styled-components';

export const PortfolioCardContainer = styled.div`
   display:flex;
   justify-content:center;
   width:100%;
   height:100vh;
   background-color:var(--bg);

`;

export const PortfolioCardLayout=styled.div`
    display:flex;
    flex-wrap:wrap;
    text-align:center;
    width:345px;
    height:90vh;
    background-color:rgba(0, 0, 0, 0.2);
    border-radius:1rem;
`;

export const PortfolioCardBox=styled.div`
    display:flex;
    padding:1rem;
    justify-content:center;
    align-items:center;
    flex-wrap:wrap;
    gap:2rem;
    `;
